class imgbrick extends StatefulWidget {
  final image;
  const imgbrick(this.image);
  @override
  _imgbrickState createState() => _imgbrickState();
}

class _imgbrickState extends State<imgbrick> {
  var opacity_of_image = 1.0, visible = 0.0;
  @override
  Widget build(BuildContext context) {
    return new InkWell(
        onTap: () {},
        onHover: (state) {
          if (state) {
            setState(() {
              opacity_of_image = 0.5;
              visible = 1.0;
            });
          } else {
            setState(() {
              opacity_of_image = 1.0;
              visible = 0.0;
            });
          }
        },
        child: Stack(
          children: [
            Opacity(
              opacity: opacity_of_image,
              child: Card(
                color: const Color(0x00000000),
                elevation: 3.0,
                child: new InkWell(
                  onTap: () {},
                  onHover: (state) {},
                  child: new Container(
                      decoration: new BoxDecoration(
                    image: new DecorationImage(
                      image: new NetworkImage(widget.image),
                      fit: BoxFit.cover,
                    ),
                    borderRadius:
                        new BorderRadius.all(const Radius.circular(10.0)),
                  )),
                ),
              ),
            ),
            Positioned(
              top: 20.0,
              right: 20.0,
              child: InkWell(
                  onTap: () {},
                  child: TextButton(
                    onPressed: () {},
                    child: (visible == 1.0) ? Text('Save') : Text(''),
                    style: TextButton.styleFrom(
                      textStyle: TextStyle(
                        fontSize: 18,
                      ),
                      primary: Colors.white,
                      backgroundColor: (visible == 1.0) ? Colors.red : null,
                      padding: EdgeInsets.all(20),
                    ),
                  )),
            ),
            Positioned(
              right: 180.0,
              bottom: 20.0,
              child: InkWell(
                  onTap: () {},
                  child: TextButton(
                    onPressed: () {},
                    child: (visible == 1.0) ? Text('Link To') : Text(''),
                    style: TextButton.styleFrom(
                      textStyle: TextStyle(
                        fontSize: 18,
                      ),
                      primary: Colors.white,
                      backgroundColor: (visible == 1.0) ? Colors.grey : null,
                      padding: EdgeInsets.all(20),
                    ),
                  )),
            ),
            Positioned(
              right: 20.0,
              bottom: 20.0,
              child: InkWell(
                onTap: () {},
                onHover: (value) {},
                child: FloatingActionButton(
                    elevation: 0.0,
                    backgroundColor:
                        (visible == 1.0) ? Colors.grey : Colors.transparent,
                    child: (visible == 1.0) ? Icon(Icons.more_horiz) : null,
                    onPressed: () {}),
              ),
            ),
            Positioned(
              bottom: 20.0,
              right: 100.0,
              child: InkWell(
                onTap: () {},
                onHover: (value) {},
                child: FloatingActionButton(
                    elevation: 0.0,
                    backgroundColor:
                        (visible == 1.0) ? Colors.grey : Colors.transparent,
                    child: (visible == 1.0) ? Icon(Icons.share) : null,
                    onPressed: () {}),
              ),
            ),
          ],
        ));
  }
}
